import tkinter as tk
import subprocess
import sys
from tkinter import Canvas, Entry, Text, Button, PhotoImage, Scrollbar, Frame
from pathlib import Path
from datasets import load_dataset

# Load the TinyStories dataset
dataset = load_dataset("roneneldan/TinyStories")

# Define the get_story_excerpts function
def get_story_excerpts(dataset, num_stories=9):
    excerpts = []
    for i in range(min(num_stories, len(dataset['train']))):
        story = dataset['train'][i]['text']
        # Use the first sentence or first 100 characters as an excerpt
        excerpt = story.split('.')[0] + '.' if '.' in story else story[:100] + '...'
        excerpts.append((i, excerpt))
    return excerpts


# Function to open ReadingSession.py and pass the story to it
def show_story(story_text):
    subprocess.Popen([sys.executable, "ReadingSession.py", story_text])

# Paths
OUTPUT_PATH = Path(__file__).parent
ASSETS_PATH = OUTPUT_PATH / Path(r"assets\\frame4")

def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

