#instruction
canvas.create_text(
    440.0,
    92.0,
    anchor="center",
    text="Select a story",
    fill="#000000",
    font=("Inter Medium", 40 * -1)
)

# Get excerpts to display as choices
excerpts = get_story_excerpts(dataset)

# Number of columns for grid layout
columns = 3

for i, (index, excerpt) in enumerate(excerpts):
    # Create a frame for each story block
    frame = tk.Frame(root, relief=tk.RAISED, borderwidth=3)
    frame.place(x=(i%columns)*320 + 10, y=(i//columns)*150 + 130)

    # Add a label with the story excerpt
    label = tk.Label(frame, text=f"Story {index + 1}: {excerpt}", wraplength=200)
    label.pack(padx=10, pady=10)

    # Add a button to read the story
    button = tk.Button(frame, text="Read Story", 
                       command=lambda i=index: show_story(dataset['train'][i]['text']))
    button.pack(padx=10, pady=10)

root.mainloop()